mapper.select:
SELECT COUNT(*)
FROM t_new_post_collected
WHERE post_id = #{postId}
  AND collected_user_id = #{userId}

mapper.delete:
DELETE FROM t_new_post_collected
WHERE post_id = #{postId}
  AND collected_user_id = #{userId}

mapper.select:
SELECT COUNT(*)
FROM t_new_post_collected
WHERE collected_user_id = #{userId}
